import React, { useState, useEffect } from 'react';

const ExperienceForm = ({ onSubmit, initialData, onCancel }) => {
  const [formData, setFormData] = useState({
    title: '',
    company: '',
    period: '',
    location: '',
    description: '',
    achievements: '',
    technologies: '',
    current: false
  });

  useEffect(() => {
    if (initialData) {
      setFormData({
        ...initialData,
        technologies: Array.isArray(initialData.technologies)
          ? initialData.technologies.join(', ')
          : initialData.technologies || '',
        achievements: Array.isArray(initialData.achievements)
          ? initialData.achievements.join(', ')
          : initialData.achievements || ''
      });
    }
  }, [initialData]);

  const handleChange = (e) => {
    const { name, value, type, checked } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: type === 'checkbox' ? checked : value
    }));
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    const submitData = {
      ...formData,
      technologies: formData.technologies.split(',').map(tech => tech.trim()).filter(tech => tech)
    };
    onSubmit(submitData);
    if (!initialData) {
      setFormData({
        company: '',
        position: '',
        startDate: '',
        endDate: '',
        description: '',
        technologies: '',
        location: '',
        current: false
      });
    }
  };

  return (
    <form onSubmit={handleSubmit} className="experience-form">
      <div className="form-group">
        <label htmlFor="company">Empresa:</label>
        <input
          type="text"
          id="company"
          name="company"
          value={formData.company}
          onChange={handleChange}
          required
        />
      </div>

      <div className="form-group">
        <label htmlFor="position">Cargo:</label>
        <input
          type="text"
          id="position"
          name="position"
          value={formData.position}
          onChange={handleChange}
          required
        />
      </div>

      <div className="form-group">
        <label htmlFor="location">Ubicación:</label>
        <input
          type="text"
          id="location"
          name="location"
          value={formData.location}
          onChange={handleChange}
        />
      </div>

      <div className="form-row">
        <div className="form-group">
          <label htmlFor="startDate">Fecha de inicio:</label>
          <input
            type="date"
            id="startDate"
            name="startDate"
            value={formData.startDate}
            onChange={handleChange}
            required
          />
        </div>

        <div className="form-group">
          <label htmlFor="endDate">Fecha de fin:</label>
          <input
            type="date"
            id="endDate"
            name="endDate"
            value={formData.endDate}
            onChange={handleChange}
            disabled={formData.current}
          />
        </div>
      </div>

      <div className="form-group checkbox-group">
        <label htmlFor="current">
          <input
            type="checkbox"
            id="current"
            name="current"
            checked={formData.current}
            onChange={handleChange}
          />
          Trabajo actual
        </label>
      </div>

      <div className="form-group">
        <label htmlFor="description">Descripción:</label>
        <textarea
          id="description"
          name="description"
          value={formData.description}
          onChange={handleChange}
          rows="4"
          required
        />
      </div>

      <div className="form-group">
        <label htmlFor="technologies">Tecnologías (separadas por coma):</label>
        <input
          type="text"
          id="technologies"
          name="technologies"
          value={formData.technologies}
          onChange={handleChange}
          placeholder="React, Node.js, MongoDB"
        />
      </div>

      <div className="form-actions">
        <button type="submit" className="btn-submit">
          {initialData ? 'Actualizar' : 'Crear'} Experiencia
        </button>
        {onCancel && (
          <button type="button" onClick={onCancel} className="btn-cancel">
            Cancelar
          </button>
        )}
      </div>
    </form>
  );
};

export default ExperienceForm;
