import React from 'react';

const ExperienceList = ({ experiences, onEdit, onDelete }) => {
  const formatDate = (dateString) => {
    if (!dateString) return '';
    const date = new Date(dateString);
    return date.toLocaleDateString('es-ES', { year: 'numeric', month: 'long' });
  };

  return (
    <div className="experience-list">
      {experiences.length === 0 ? (
        <p className="no-experiences">No hay experiencias profesionales registradas.</p>
      ) : (
        experiences.map((experience) => (
          <div key={experience.id} className="experience-card">
            <div className="experience-header">
              <h3>{experience.position}</h3>
              <div className="experience-actions">
                <button
                  onClick={() => onEdit(experience)}
                  className="btn-edit"
                  title="Editar"
                >
                  ‚úèÔ∏è
                </button>
                <button
                  onClick={() => onDelete(experience.id)}
                  className="btn-delete"
                  title="Eliminar"
                >
                  üóëÔ∏è
                </button>
              </div>
            </div>

            <div className="experience-company">
              <strong>{experience.company}</strong>
              {experience.location && <span> - {experience.location}</span>}
            </div>

            <div className="experience-dates">
              {formatDate(experience.startDate)} - {
                experience.current ? 'Presente' : formatDate(experience.endDate)
              }
            </div>

            <div className="experience-description">
              <p>{experience.description}</p>
            </div>

            {experience.technologies && experience.technologies.length > 0 && (
              <div className="experience-technologies">
                <strong>Tecnolog√≠as:</strong>
                <div className="technologies-list">
                  {experience.technologies.map((tech, index) => (
                    <span key={index} className="technology-tag">
                      {tech}
                    </span>
                  ))}
                </div>
              </div>
            )}
          </div>
        ))
      )}
    </div>
  );
};

export default ExperienceList;
