import React from 'react';
import { Link } from 'react-router-dom';
import Skills from '../components/Skills';

const Home = () => {
  const scrollToSkills = () => {
    document.getElementById('skills').scrollIntoView({ behavior: 'smooth' });
  };

  return (
    <div className="contenedor-grid">
      <main className="main-home">
        <section className="contenido-principal-home">
          <div className="contenido-principal-home-info">
            <h3 className="contenido-principal-home-info-nombre">Hola, soy Luis Yepes <span className="manito"><i className="fa-regular fa-hand-peace"></i></span></h3>

            <h2 className="contenido-principal-home-info-carrera"><span>Ingeniero</span> de Software en formaci√≥n</h2>

            <p className="contenido-principal-home-info-descripcion">Soy un futuro ingeniero de software apasionado por el desarrollo de aplicaciones y sistemas
              inteligentes. Mi objetivo es crear soluciones eficientes y elegantes que faciliten la vida de
              las personas y conecten mejor el mundo digital.</p>

            <div className="contenido-principal-home-botones">
              <Link to="/proyectos" className="button-show-projects">Ver mis proyectos</Link>
              <button className="button-show-skills" onClick={scrollToSkills}>Conoce mis Skills</button>
            </div>
          </div>

          <div className="contenido-principal-home-foto">
            <img src="/img/circulo_recortado_original.png" alt="Foto personal" className="foto-personal" />
          </div>
        </section>

        <Skills />
      </main>
    </div>
  );
};

export default Home;
